	public boolean Connect() {
		api = new IOTX(iotex_url+":"+iotex_port);
		
		RPCMethod connection = api.currentProvider();
		if(connection.toString().isEmpty()) {
			return false;
		}
		
		return true;
	}

	public void Send(List<Transaction> messages) {

    	messages.forEach(message->{
    		String hash = contract.execute(null, 100000l, "1000000000000", account, "setTransaction", message.getData());
    		System.out.println(hash);
    	});
    	
	}

	public void Configure(String s) {
		account = IotexAccount.create(s);	
		contract = new Contract(api.currentProvider(), CONTRACT_ABI, Numeric.hexStringToByteArray(CONTRACT_BIN));

		String hash = contract.deploy(null, 100000l, "1000000000000", account, "0", 5);
		System.out.println(hash);
	}

